use std::str::Chars;

fn climb_stairs(plan: String) -> i32 {
    plan.chars().map( |x| {
        match x {
            '(' => 1,
            ')' => -1,
            _ => panic!("Wrong!")
        }
    }).sum()
}

fn find_basement_entry_point(plan: String) -> i32 {
    follow_instruction(0, 0, plan.chars())
}

fn follow_instruction(step_count: i32, floor: i32, plan: Chars<'_>) -> i32 {
    let direction = match plan.clone().nth(step_count as usize) {
        Some(c) => {
            match c {
                '(' => 1,
                ')' => -1,
                _ => panic!("wrong input")
            }
        }
        None => return floor
    };
    if floor + direction == -1 {
        return step_count + 1;
    }
    follow_instruction(step_count + 1, floor + direction, plan)
}

